// Learn about diffmatchpatch handling of invalid utf8 input.
package main

import (
	"fmt"
	"io/ioutil"
	"net/http"
	"unicode/utf8"

	"github.com/sergi/go-diff/diffmatchpatch"
	"github.com/shurcooL/go-goon"
	"github.com/shurcooL/go/openutil"
)

func main() {
	var inputs = []string{
		"a1234567890z",
		"Hello 世界",
		"a\xe0\xe5\xf0\xe9\xe1\xf8\xf1\xe9\xe8\xe4Z",
	}

	for _, in := range inputs {
		fmt.Printf("input: %q\n(length %v bytes, %v runes)\nutf8.Valid: %v\n", in, len(in), utf8.RuneCountInString(in), utf8.ValidString(in))

		dmp := diffmatchpatch.New()
		diffs := dmp.DiffMain(in, "", true)

		fmt.Printf("diff text: %q\n(length %v bytes, %v runes)\n\n", diffs[0].Text, len(diffs[0].Text), utf8.RuneCountInString(diffs[0].Text))
	}
}

